import { ScrollView, Text } from "react-native";

export function UseEffectScreen() {
    return (
        <ScrollView>
            <Text>useEffect precisa de 2 argumentos, callback e dependências. Assim, quando o componente funciona, a callback é chamada se alguma dependência mudou desde a última </Text>  
        </ScrollView>
    );
}
