import React from "react";
import { ScrollView, Text } from "react-native";

export function UseStateScreen() {
    return (
        <ScrollView>
            <Text>O useState Hook tem como função retornar um valor e uma função para atualizar valores, já que pega o estado do objeto e o modifica durante o render.
            </Text>  
        </ScrollView>
    );
}
